import React, { useState } from 'react';
import MatrixVideoBackground from './components/MatrixVideoBackground';
import MatrixLanding from './components/MatrixLanding';
import './App.css';

function App() {
  const [showPortal, setShowPortal] = useState(false);
  const [showPasswordInput, setShowPasswordInput] = useState(false);
  const [password, setPassword] = useState('');

  const handleEnterPortal = () => {
    setShowPortal(true);
  };

  const handlePortalButtonClick = () => {
    setShowPasswordInput(true);
  };

  const handlePasswordSubmit = (e) => {
    e.preventDefault();
    // Handle password validation here
    console.log('Password entered:', password);
    // You can add password validation logic here
  };

  if (!showPortal) {
    return <MatrixLanding onEnter={handleEnterPortal} />;
  }

  return (
    <div className="relative min-h-screen">
      {/* Matrix Video Background */}
      <MatrixVideoBackground 
        theme="green"
        settings={{
          opacity: 0.85,
          blur: 0,
          interactive: true,
          useYouTube: true
        }}
      />
      
      {/* Drepheus Portal Content */}
      <div className="relative z-10 flex flex-col items-center justify-center min-h-screen text-white">
        <div className="text-center mb-8 px-8 bg-black/40 backdrop-blur-sm rounded-lg p-12 border border-green-400/20">
          <h1 className="text-5xl md:text-7xl font-bold mb-4 text-green-400" style={{
            fontFamily: 'Courier New, monospace',
            textShadow: '0 0 10px #00ff00, 0 0 20px #00ff00, 0 0 40px #00ff00, 0 0 80px #00ff00',
            filter: 'brightness(1.2)',
            letterSpacing: '0.2em',
            fontWeight: '900',
            textTransform: 'uppercase',
            WebkitTextStroke: '1px #00ff00'
          }}>
            DREPHEUS
          </h1>
          <h2 className="text-3xl md:text-5xl font-bold mb-6 text-green-400" style={{
            fontFamily: 'Courier New, monospace',
            textShadow: '0 0 8px #00ff00, 0 0 16px #00ff00, 0 0 32px #00ff00',
            letterSpacing: '0.2em',
            fontWeight: '900',
            WebkitTextStroke: '1px #00ff00'
          }}>
            PORTAL
          </h2>
          <p className="text-lg md:text-xl font-mono text-green-300 mb-10 max-w-2xl mx-auto leading-relaxed">
            Welcome to the digital nexus. Enter the portal to access advanced trading systems and market intelligence.
          </p>
          
          {/* Portal Entry Button */}
          <button className="px-10 py-3 font-mono text-lg border-2 border-green-400 hover:bg-green-400/20 transition-all duration-300 text-green-400 hover:shadow-2xl hover:shadow-green-400/30 hover:scale-105 bg-black/50 backdrop-blur-sm">
            ENTER PORTAL
          </button>
        </div>
      </div>
    </div>
  );
}

export default App;
