import React, { useState } from 'react';
import AuthenticMatrixRain from './components/AuthenticMatrixRain';
import MatrixDemo from './components/MatrixDemo';
import './index.css';

function App() {
  const [showDemo, setShowDemo] = useState(false);
  const [theme, setTheme] = useState('green');

  return (
    <div className="relative min-h-screen">
      {/* Authentic Matrix Background */}
      <AuthenticMatrixRain 
        theme={theme}
        settings={{
          streamDensity: 40,
          glitchIntensity: 0.015,
          ambientParticles: true,
          motionBlur: true,
          interactive: true
        }}
      />
      
      {/* Demo Overlay */}
      {showDemo && (
        <MatrixDemo onClose={() => setShowDemo(false)} />
      )}
      
      {/* Content Layer */}
      <div className="relative z-10 flex flex-col items-center justify-center min-h-screen text-white">
        <div className="text-center mb-8 px-4">
          <h1 className="text-6xl md:text-8xl font-mono font-bold mb-4 bg-gradient-to-r from-green-400 to-green-600 bg-clip-text text-transparent">
            THE MATRIX
          </h1>
          <p className="text-xl md:text-2xl font-mono text-green-300 mb-8">
            Follow the white rabbit...
          </p>
          
          {/* Theme Selector */}
          <div className="flex justify-center gap-4 mb-8">
            {['green', 'blue', 'gold', 'white'].map((color) => (
              <button
                key={color}
                onClick={() => setTheme(color)}
                className={`px-4 py-2 font-mono text-sm border rounded transition-all duration-300 ${
                  theme === color 
                    ? 'border-green-400 bg-green-400/20 text-green-400 shadow-lg shadow-green-400/20' 
                    : 'border-gray-600 hover:border-gray-400 text-gray-400 hover:bg-gray-800/20'
                }`}
              >
                {color.toUpperCase()}
              </button>
            ))}
          </div>
          
          {/* Demo Button */}
          <button
            onClick={() => setShowDemo(true)}
            className="px-8 py-3 font-mono text-lg border border-green-400 hover:bg-green-400/20 transition-all duration-300 text-green-400 hover:shadow-lg hover:shadow-green-400/20 hover:scale-105"
          >
            ENTER THE MATRIX
          </button>
        </div>
      </div>
    </div>
  );
}

export default App;
