import React, { useState, useEffect } from 'react';
import './SimpleMatrixLanding.css';

const SimpleMatrixLanding = ({ onEnter }) => {
  const [showMatrix, setShowMatrix] = useState(false);
  
  useEffect(() => {
    // Show matrix after a short delay
    const timer = setTimeout(() => {
      setShowMatrix(true);
    }, 1000);
    
    return () => clearTimeout(timer);
  }, []);

  return (
    <div className="simple-matrix-container">
      {/* CSS Matrix Rain Effect */}
      <div className="matrix-rain">
        {showMatrix && Array.from({ length: 30 }, (_, i) => (
          <div
            key={i}
            className="matrix-column"
            style={{
              left: `${(i * 3.33)}%`,
              animationDelay: `${Math.random() * 5}s`,
              animationDuration: `${3 + Math.random() * 4}s`
            }}
          >
            {Array.from({ length: 15 }, (_, j) => (
              <span
                key={j}
                className="matrix-char"
                style={{
                  animationDelay: `${j * 0.1}s`
                }}
              >
                {String.fromCharCode(65 + Math.random() * 26)}
              </span>
            ))}
          </div>
        ))}
      </div>

      {/* Drepheus Title */}
      <div className="title-container">
        <h1 className="drepheus-title">
          DREPHEUS
        </h1>
      </div>

      {/* Loading Message */}
      {!showMatrix && (
        <div className="loading-container">
          <div className="loading-text">
            Initializing matrix...
          </div>
        </div>
      )}

      {/* Click overlay */}
      <div className="click-overlay" onClick={onEnter}>
        <div className="click-hint">
          <p className="click-text">Click anywhere to enter</p>
          <div className="arrow-down">â†“</div>
        </div>
      </div>
    </div>
  );
};

export default SimpleMatrixLanding;
