import React, { useState, useEffect } from 'react';

const SimpleMatrixLanding = ({ onEnter }) => {
  const [showMatrix, setShowMatrix] = useState(false);
  
  useEffect(() => {
    // Show matrix after a short delay
    const timer = setTimeout(() => {
      setShowMatrix(true);
    }, 1000);
    
    return () => clearTimeout(timer);
  }, []);

  return (
    <div className="relative w-full h-screen bg-black overflow-hidden">
      {/* CSS Matrix Rain Effect */}
      <div className="matrix-container">
        {showMatrix && Array.from({ length: 50 }, (_, i) => (
          <div
            key={i}
            className="matrix-column"
            style={{
              left: `${(i * 2)}%`,
              animationDelay: `${Math.random() * 5}s`,
              animationDuration: `${3 + Math.random() * 4}s`
            }}
          >
            {Array.from({ length: 20 }, (_, j) => (
              <span
                key={j}
                className="matrix-char"
                style={{
                  animationDelay: `${j * 0.1}s`
                }}
              >
                {String.fromCharCode(0x30A0 + Math.random() * 96)}
              </span>
            ))}
          </div>
        ))}
      </div>

      {/* Drepheus Title */}
      <div className="absolute inset-0 flex items-center justify-center">
        <h1 className="text-6xl md:text-8xl font-mono text-green-400 glow-text animate-pulse">
          DREPHEUS
        </h1>
      </div>

      {/* Loading Message */}
      {!showMatrix && (
        <div className="absolute inset-0 flex items-center justify-center">
          <div className="text-green-400 font-mono text-xl animate-pulse">
            Initializing matrix...
          </div>
        </div>
      )}

      {/* Click overlay */}
      <div 
        className="absolute inset-0 flex items-center justify-center cursor-pointer z-10"
        onClick={onEnter}
      >
        <div className="text-center opacity-0 hover:opacity-100 transition-opacity duration-500 mt-32">
          <p className="text-green-400 text-lg font-mono mb-4">Click anywhere to enter</p>
          <div className="animate-pulse">
            <svg className="w-8 h-8 mx-auto text-green-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 14l-7 7m0 0l-7-7m7 7V3" />
            </svg>
          </div>
        </div>
      </div>

      <style jsx>{`
        .matrix-container {
          position: absolute;
          top: 0;
          left: 0;
          width: 100%;
          height: 100%;
          overflow: hidden;
        }

        .matrix-column {
          position: absolute;
          top: -100%;
          font-family: 'Courier New', monospace;
          font-size: 14px;
          color: #39ff14;
          animation: fall linear infinite;
          text-shadow: 0 0 5px #39ff14;
        }

        .matrix-char {
          display: block;
          opacity: 0;
          animation: fadeInOut 0.5s ease-in-out infinite alternate;
        }

        @keyframes fall {
          to {
            top: 100%;
          }
        }

        @keyframes fadeInOut {
          0% { opacity: 0; }
          100% { opacity: 1; }
        }

        .glow-text {
          text-shadow: 
            0 0 10px #39ff14,
            0 0 20px #39ff14,
            0 0 30px #39ff14,
            0 0 40px #39ff14;
        }
      `}</style>
    </div>
  );
};

export default SimpleMatrixLanding;
