import React from 'react';
import MatrixVideoBackground from './components/MatrixVideoBackground';
import './index.css';

function App() {
  return (
    <div className="relative min-h-screen">
      {/* Matrix Video Background */}
      <MatrixVideoBackground 
        theme="green"
        settings={{
          opacity: 0.85,
          blur: 0,
          interactive: true,
          useYouTube: true
        }}
      />
      
      {/* Drepheus Portal Content */}
      <div className="relative z-10 flex flex-col items-center justify-center min-h-screen text-white">
        <div className="text-center mb-8 px-8 bg-black/40 backdrop-blur-sm rounded-lg p-12 border border-green-400/20">
          <h1 className="text-7xl md:text-9xl font-mono font-bold mb-6 bg-gradient-to-r from-green-400 via-green-300 to-green-500 bg-clip-text text-transparent">
            DREPHEUS
          </h1>
          <h2 className="text-4xl md:text-6xl font-mono font-bold mb-8 text-green-400">
            PORTAL
          </h2>
          <p className="text-xl md:text-2xl font-mono text-green-300 mb-12 max-w-2xl mx-auto leading-relaxed">
            Welcome to the digital nexus. Enter the portal to access advanced trading systems and market intelligence.
          </p>
          
          {/* Portal Entry Button */}
          <button className="px-12 py-4 font-mono text-xl border-2 border-green-400 hover:bg-green-400/20 transition-all duration-300 text-green-400 hover:shadow-2xl hover:shadow-green-400/30 hover:scale-105 bg-black/50 backdrop-blur-sm">
            ENTER PORTAL
          </button>
        </div>
      </div>
    </div>
  );
}

export default App;
